    let self = this;
    var config = {
      method: 'post',
      url: 'http://localhost:5232/usuario/register',
      headers: { 
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + localStorage.getItem('authTokenAdm')
      },
